import { Field } from "formik";
import React from "react";

const FormikInput = ({label, id, type, name, onChange}) => {
  return (
    <>
      <Field name={name}>
        {({ field, form, meta }) => {
          // console.log(meta)
          return (
            <>
              <label htmlFor={id}>{label}: </label>
              <input
                type={type}
                id={id}
                value={meta.value}
                onChange={onChange}
              ></input>
              {meta.touched && meta.error?<div>Name field is required</div>:null}
            </>
          );
        }}
      </Field>
    </>
  );
};

export default FormikInput;
