import React from "react";

const FormikRadio = ({ options, name, onChange}) => {
  return (
    <>
      <Field name={name}>
        {({ field, form, meta }) => {
          return (
            <>
              {options.map((option, i) => {
                <div key={i}>
                  <label htmlFor={option.value}>{option.label}: </label>
                  <input type="radio" id={option.value} checked={meta.value === option.value} onChange={onChange}></input>
                </div>;
              })}
            </>
          );
        }}
      </Field>
    </>
  );
};

export default FormikRadio;
