import axios from 'axios';
import React, { useEffect, useState } from 'react'

const ShowAllHTLocation = () => {
  let [rating, setRating] = useState(0);
  let [index, setIndex] = useState(0);
  let [hTLocations, setHTLocations] = useState([]);

  let getAllHTLocation = async() => {
 try {
     let result = await axios({
       url: `http://localhost:8000/hTLocations`,
       method: `get`,
     });
     setHTLocations(result.data.result);
     console.log(result);
 } catch (error) {
    console.log(error);
 }
  };

  useEffect(() => {
    getAllHTLocation();
  }, [])
  return (
    <>
      {hTLocations.map((hTLocation, i) => {
        
        return (
          <div key={i} style={i%2===0?{backgroundColor: "lightblue"}:{backgroundColor: "lightgoldenrodyellow"}}>
            <p>Name: {hTLocation.hTName}</p>
            <p>Location: {hTLocation.location}</p>
            <p>Difficult: {hTLocation.difficulty}</p>
            <p>Length: {hTLocation.length.value}{hTLocation.length.unit}</p>
            <button></button>
            <button onClick={() => {}}>Edit</button>
            <button>Add to Wishlist</button>
            <form onSubmit = {() => {}}>
              <label htmlFor="rating">Rating: </label>
              <input type="number" min="0" max="5" id="rating" value={rating} onChange={(e) => {setRating(e.target.value)}}></input>
              <button type="submit">submit rating</button>
            </form>
            
          </div>
        )
      })}
    </>
  )
}

export default ShowAllHTLocation
